func (v UserValue) ToObjectValue(ctx context.Context) (basetypes.ObjectValue, diag.Diagnostics) {
	var diags diag.Diagnostics

	attributeTypes := map[string]attr.Type{
		"email":               basetypes.StringType{},
		"first_name":          basetypes.StringType{},
		"full_name":           basetypes.StringType{},
		"full_name_with_team": basetypes.StringType{},
		"id":                  basetypes.StringType{},
		"last_name":           basetypes.StringType{},
		"time_zone":           basetypes.StringType{},
	}

	if v.IsNull() {
		return types.ObjectNull(attributeTypes), diags
	}

	if v.IsUnknown() {
		return types.ObjectUnknown(attributeTypes), diags
	}

	objVal, diags := types.ObjectValue(
		attributeTypes,
		map[string]attr.Value{
			"email":               v.Email,
			"first_name":          v.FirstName,
			"full_name":           v.FullName,
			"full_name_with_team": v.FullNameWithTeam,
			"id":                  v.Id,
			"last_name":           v.LastName,
			"time_zone":           v.TimeZone,
		})

	return objVal, diags
}

func (v UserValue) Equal(o attr.Value) bool {
	other, ok := o.(UserValue)

	if !ok {
		return false
	}

	if v.state != other.state {
		return false
	}

	if v.state != attr.ValueStateKnown {
		return true
	}

	if !v.Email.Equal(other.Email) {
		return false
	}

	if !v.FirstName.Equal(other.FirstName) {
		return false
	}

	if !v.FullName.Equal(other.FullName) {
		return false
	}

	if !v.FullNameWithTeam.Equal(other.FullNameWithTeam) {
		return false
	}

	if !v.Id.Equal(other.Id) {
		return false
	}

	if !v.LastName.Equal(other.LastName) {
		return false
	}

	if !v.TimeZone.Equal(other.TimeZone) {
		return false
	}

	return true
}

